domain_model.plantuml:
@startuml
class System {
  + monitor()
  + sendNotification()
  + acceptUserInput()
  + record()
  + presentReport()
  + persistData()
}

class WaterFiltrationSystem {
  + waterPassed: float
}

class WaterSoftener {
  + saltLevel: float
}

class AirFilter

class RadonDetector {
  + radonLevel: float
}

class Zone {
  + desiredTemperature: float
}

class Window {
  + open()
  + close()
  + reverseDirection()
}

class Appliance {
  + run()
}

class PetWaterBowl {
  + waterConsumption: float
}

class PetFoodBowl {
  + portionWeight: float
}

class CoffeeMaker {
  + beanLevel: float
  + start()
  + stop()
}

class HotTub {
  + waterTemperature: float
  + waterLevel: float
  + pHLevel: float
  + bacterialLevel: float
  + openCover()
  + closeCover()
}

class IrrigationDevice {
  + runTestCycle()
}

class Driveway {
  + surfaceTemperature: float
}

class Recipe

class PhoneMailbox

System "1" -- "1" WaterFiltrationSystem
System "1" -- "1" WaterSoftener
System "1" -- "1" AirFilter
System "1" -- "1" RadonDetector
System "1" -- "1..*" Zone
System "1" -- "1..*" Window
System "1" -- "0..*" Appliance
System "1" -- "1..*" PetWaterBowl
System "1" -- "1..*" PetFoodBowl
System "1" -- "0..1" CoffeeMaker
System "1" -- "0..1" HotTub
System "1" -- "0..*" IrrigationDevice
System "1" -- "0..1" Driveway
System "1" -- "0..*" Recipe
System "1" -- "0..*" PhoneMailbox
@enduml
